import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../firebase';

const DownloadDataPage = () => {
  const [formData, setFormData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const formDataCollection = collection(db, 'formData');
      const formDataSnapshot = await getDocs(formDataCollection);
      const formDataData = formDataSnapshot.docs.map(doc => doc.data());
      setFormData(formDataData);
    };

    fetchData();
  }, []);

  const convertToCSV = (data) => {
    const header = Object.keys(data[0]).join(',');
    const rows = data.map(obj => Object.values(obj).join(','));
    return `${header}\n${rows.join('\n')}`;
  };

  const handleDownload = () => {
    const csvData = convertToCSV(formData);
    const blob = new Blob([csvData], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'formData.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-semibold mb-4">Download Data</h2>
      <div className="mb-8">
        <button onClick={handleDownload} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded inline-block">Download CSV</button>
      </div>
      <div>
        <h3 className="text-xl font-semibold mb-4">Form Data List</h3>
        <ul className="border border-gray-200 rounded-lg divide-y divide-gray-200">
          {formData.map((data, index) => (
            <li key={index} className="p-4 hover:bg-gray-50">
              <p className="text-lg font-medium">{data.name}</p>
              <p className="text-gray-500">{data.email}</p>
              <p className="text-gray-500">{data.phone}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default DownloadDataPage;
