import React, { useEffect, useState } from 'react';
import { collection, getDocs, deleteDoc, doc } from 'firebase/firestore';
import { db } from '../firebase';

const DownloadDataPage = () => {
  const [formData, setFormData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const formDataCollection = collection(db, 'formData');
      const formDataSnapshot = await getDocs(formDataCollection);
      const formDataData = formDataSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setFormData(formDataData);
    };

    fetchData();
  }, []);

  const convertToCSV = (data) => {
    const header = Object.keys(data[0]).join(',');
    const rows = data.map(obj => Object.values(obj).join(','));
    return `${header}\n${rows.join('\n')}`;
  };

  const handleDownload = () => {
    const csvData = convertToCSV(formData);
    const blob = new Blob([csvData], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'formData.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const handleDelete = async (id) => {
    try {
      await deleteDoc(doc(db, 'formData', id));
      setFormData(formData.filter(item => item.id !== id));
    } catch (error) {
      console.error('Error deleting document: ', error);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-semibold mb-4">Download Data</h2>
      <div className="mb-8">
        <button onClick={handleDownload} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded inline-block mb-4">Download CSV</button>
      </div>
      <div>
        <h3 className="text-xl font-semibold mb-4">Form Data List</h3>
        <div className="flex flex-wrap -mx-4">
          {formData.map((data) => (
            <div key={data.id} className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 px-4 mb-4">
              <div className="bg-white p-4 rounded-lg shadow-md flex justify-between items-center">
                <div>
                  <p className="text-lg font-medium">{data.name}</p>
                  <p className="text-gray-500">{data.email}</p>
                  <p className="text-gray-500">{data.phone}</p>
                </div>
                <button onClick={() => handleDelete(data.id)} className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded">Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DownloadDataPage;
