import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const signIn = async () => {
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      if (user.email === 'admin@aitgaint.com') {
        navigate('/Dashboard');
      } else {
        throw new Error('Only for admin');
      }

      toast.success('Login successful!', { autoClose: 2000 });
    } catch (error) {
      toast.error(error.message);
    }
  };

  return (
    <section className="h-screen flex flex-col md:flex-row justify-center items-center bg-gray-100">
      <div className="md:w-1/3 max-w-sm p-6">
        <img src="https://tecdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp" alt="Sample" className="rounded-lg shadow-lg" />
      </div>
      <div className="md:w-1/3 max-w-sm p-6 bg-white rounded-lg shadow-lg">
        <input 
          className="text-sm w-full px-4 py-2 border border-solid border-gray-300 rounded mb-4" 
          type="text" 
          placeholder="Email Address" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
        />
        <div className="relative">
          <input 
            className="text-sm w-full px-4 py-2 border border-solid border-gray-300 rounded mb-4" 
            type={showPassword ? 'text' : 'password'} 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
          <button 
            className="absolute inset-y-0 right-0 pr-3 flex items-center text-sm leading-5" 
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? 'Hide' : 'Show'}
          </button>
        </div>
        <div className="text-center md:text-left">
          <button 
            onClick={signIn} 
            className="w-full mt-4 bg-blue-600 hover:bg-blue-700 px-4 py-2 text-white uppercase rounded text-xs tracking-wider"
          >
            Login
          </button>
        </div>
      </div>
      <ToastContainer />
    </section>
  );
};

export default Login;
